import React, { useState } from 'react';
import './App.css';
import Headerbar from './Headerbar';
import Function from './Function';
import Avilable from './Avilable';
import Favourites from './Favourites';
import Footer from './Footer';
import jsonData from './data.json';

function App() {
  const [isAdvancedSearchVisible, setIsAdvancedSearchVisible] = useState(false);

  const [filteredDataHead, setFilteredData]=useState(jsonData);
  const saleSearch=(searchTerm)=>{
    const filteredResults = jsonData.properties.filter(item =>
      item.id.toLowerCase().includes(searchTerm.toLowerCase()));
    setFilteredData(filteredResults);
    console.log(searchTerm);
  }

  const toggleAdvancedSearch = () => {
    setIsAdvancedSearchVisible(!isAdvancedSearchVisible);
  };

  return (
    <div className="App">
      <Headerbar saleSearch={saleSearch}/>

      {isAdvancedSearchVisible && (
        <div className='app-inner-div'>
          <lable>type</lable>
          <form>
            <label for="dropdown">Select an option:</label>
            <select id="dropdown" name="dropdown">
              <option value="option1">Option 1</option>
              <option value="option2">Option 2</option>
              <option value="option3">Option 3</option>
            </select>
          </form>
          <lable>price</lable>
          <input type="text"></input>
          <input type="text"></input>
          <lable>bedrooms</lable>
          <input type="text"></input>
          <input type="text"></input>
          <lable>date added</lable>
          <input type="date"></input>
          <input type="date"></input>
          <lable>tenure</lable>
          <form>
            <label for="dropdown">Select an option:</label>
            <select id="dropdown" name="dropdown">
              <option value="option1">Option 1</option>
              <option value="option2">Option 2</option>
              <option value="option3">Option 3</option>
            </select>
          </form>
          <button> search </button>
        </div>
      )}
      <div className='App-innerdiv'>
        <Function filteredDataHead={filteredDataHead}/>
        {/*<Avilable />*/}
        {/*<Favourites />*/}
      </div>
      <Footer />
    </div>
  );
}

export default App;
