import React, { useState, useEffect } from 'react';
import './Function.css';

// Import data directly
import jsonData from './data.json';

const App = () => {

  const [isAdvancedSearchVisible, setIsAdvancedSearchVisible] = useState(false);

  const toggleAdvancedSearch = () => {
    setIsAdvancedSearchVisible(!isAdvancedSearchVisible);
  };



  const [searchId, setSearchId] = useState('');
  const [minPrice, setMinPrice] = useState('');
  const [maxPrice, setMaxPrice] = useState('');
  const [minAddedDate, setMinAddedDate] = useState('');
  const [maxAddedDate, setMaxAddedDate] = useState('');
  const [minBedrooms, setMinBedrooms] = useState('');
  const [maxBedrooms, setMaxBedrooms] = useState('');
  const [type, setType] = useState('');
  const [postcodeArea, setPostcodeArea] = useState('');
  const [filteredData, setFilteredData] = useState(jsonData.properties);
  const [favorites, setFavorites] = useState([]);

  useEffect(() => {
    // Set filtered data initially to all data
    setFilteredData(jsonData.properties);
  }, []);

  const handleSearch = () => {
    // Filter data based on search criteria
    const filteredResults = jsonData.properties.filter(
      (item) =>
        item.id.toLowerCase().includes(searchId.toLowerCase()) &&
        (!minPrice || item.price >= parseInt(minPrice, 10)) &&
        (!maxPrice || item.price <= parseInt(maxPrice, 10)) &&
        (!minAddedDate ||
          new Date(
            item.added.year,
            getMonthIndex(item.added.month),
            item.added.day
          ) >= new Date(minAddedDate)) &&
        (!maxAddedDate ||
          new Date(
            item.added.year,
            getMonthIndex(item.added.month),
            item.added.day
          ) <= new Date(maxAddedDate)) &&
        (!minBedrooms || item.bedrooms >= parseInt(minBedrooms, 10)) &&
        (!maxBedrooms || item.bedrooms <= parseInt(maxBedrooms, 10)) &&
        (!type || item.type.toLowerCase().includes(type.toLowerCase())) &&
        (!postcodeArea ||
          item.location.toLowerCase().includes(postcodeArea.toLowerCase()))
    );

    setFilteredData(filteredResults);
  };

  const handleToggleFavorite = (item) => {
    // Check if the item is already in favorites
    const isFavorite = favorites.some((fav) => fav.id === item.id);

    if (isFavorite) {
      // Remove from favorites
      const updatedFavorites = favorites.filter((fav) => fav.id !== item.id);
      setFavorites(updatedFavorites);
    } else {
      // Add to favorites
      setFavorites([...favorites, item]);
    }
  };

  const handleDragStart = (event, item) => {
    // Set the dragged item's data
    event.dataTransfer.setData('text/plain', JSON.stringify(item));
  };

  const handleDrop = (event) => {
    event.preventDefault();
    // Get the dragged item's data
    const draggedItem = JSON.parse(event.dataTransfer.getData('text/plain'));
    handleToggleFavorite(draggedItem);
  };

  const getMonthIndex = (month) => {
    const months = [
      'January',
      'February',
      'March',
      'April',
      'May',
      'June',
      'July',
      'August',
      'September',
      'October',
      'November',
      'December',
    ];
    return months.indexOf(month);
  };

  return (
      <div>
      <h1>Search App</h1>
      <button onClick={toggleAdvancedSearch}>Advanced Search </button>
      <div className='divdiv'>
        <label>
          Search by ID:
          <input
            type="text"
            value={searchId}
            onChange={(e) => setSearchId(e.target.value)}
          />
        </label>
      </div>
      <div>
        <label>
          Minimum Price:
          <input
            type="number"
            value={minPrice}
            onChange={(e) => setMinPrice(e.target.value)}
          />
        </label>
        <label>
          Maximum Price:
          <input
            type="number"
            value={maxPrice}
            onChange={(e) => setMaxPrice(e.target.value)}
          />
        </label>
      </div>
      <div>
        <label>
          Minimum Added Date:
          <input
            type="date"
            value={minAddedDate}
            onChange={(e) => setMinAddedDate(e.target.value)}
          />
        </label>
        <label>
          Maximum Added Date:
          <input
            type="date"
            value={maxAddedDate}
            onChange={(e) => setMaxAddedDate(e.target.value)}
          />
        </label>
      </div>
      <div>
        <label>
          Minimum Bedrooms:
          <input
            type="number"
            value={minBedrooms}
            onChange={(e) => setMinBedrooms(e.target.value)}
          />
        </label>
        <label>
          Maximum Bedrooms:
          <input
            type="number"
            value={maxBedrooms}
            onChange={(e) => setMaxBedrooms(e.target.value)}
          />
        </label>
      </div>
      <div>
        <label>
          Type:
          <input
            type="text"
            value={type}
            onChange={(e) => setType(e.target.value)}
          />
        </label>
        <label>
          Postcode Area:
          <input
            type="text"
            value={postcodeArea}
            onChange={(e) => setPostcodeArea(e.target.value)}
          />
        </label>
      </div>
      <button onClick={handleSearch}>Search</button>
      
      <div>
        <h2>Search Results:</h2>
        <ul>
          {filteredData.map((item) => (
            <li
              key={item.id}
              onDragStart={(e) => handleDragStart(e, item)}
              draggable
            >
              <strong>ID:</strong> {item.id},
              <strong>Type:</strong> {item.type},
              <strong>Price:</strong> ${item.price},
              <strong>Bedrooms:</strong> {item.bedrooms},
              <strong>Date Added:</strong> {`${item.added.month} ${item.added.day}, ${item.added.year}`},
              <strong>Postcode Area:</strong> {item.location},
              <strong>Tenure:</strong> {item.tenure}
              <img
                src={item.picture}
                alt={`Image for ${item.id}`}
                style={{ maxWidth: '100px', maxHeight: '100px', marginLeft: '10px' }}
              />
            </li>
          ))}
        </ul>
      </div>
      <div onDrop={handleDrop} onDragOver={(e) => e.preventDefault()}>
        <h2>Favorite Items:</h2>
        <ul>
          {favorites.map((item) => (
            <li key={item.id}>
              <strong>ID:</strong> {item.id},
              <strong>Type:</strong> {item.type},
              <strong>Price:</strong> ${item.price},
              <strong>Bedrooms:</strong> {item.bedrooms},
              <strong>Date Added:</strong> {`${item.added.month} ${item.added.day}, ${item.added.year}`},
              <strong>Postcode Area:</strong> {item.location},
              <strong>Tenure:</strong> {item.tenure}
              <img
                src={item.picture}
                alt={`Image for ${item.id}`}
                style={{ maxWidth: '100px', maxHeight: '100px', marginLeft: '10px' }}
              />
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default App;
