import React, { useState } from 'react';
import './Avilableitem.css';

function Popup({ handleClose }) {
  return (
    <div className="popup">
      <div className="popup-inner">
        <button className="close-btn" onClick={handleClose}>
          Close
        </button>
        {/* Add content for your popup here, e.g., scrolling images and additional content */}
        <p>Popup content goes here</p>
      </div>
    </div>
  );
}

function Avilableitem() {
  const [isPopupOpen, setIsPopupOpen] = useState(false);

  const handlePopupToggle = () => {
    setIsPopupOpen(!isPopupOpen);
  };

  return (
    <div className='item-content' onClick={handlePopupToggle}>
      {/* Your existing content */}
      <img src="https://images.unsplash.com/photo-1580910051074-3eb694886505?q=80&w=1000&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxzZWFyY2h8MTR8fHBob25lfGVufDB8fDB8fHww" alt="Example image" width="200" height="150"/>
      <span>type:sampleType</span>
      <span>brand:sampleBrand</span>
      <span>location:sampleLocation</span>
      <span>added:sampleDate</span>
      <button>add to favourites</button>

      {/* Popup component */}
      {isPopupOpen && <Popup handleClose={handlePopupToggle} />}
    </div>
  );
}

export default Avilableitem;
